import React , { Component } from 'react'

export default class Counter extends Component{
    constructor(props){
        super(props)
        this.state = {
            count: props.initialCount,
            showimage: false
        }
    }
    render(){
        return(

            <div className="App">
                {this.state.showimage && (<button onClick={() => this.changestate()}>teste</button>)}
      <button onClick={() => this.changeCount(-1)}>-{this.state.showimage}</button>
      <span>{this.state.count}</span>
      <button onClick={() => this.changeCount(+1)}>+</button>
    </div>
        )}
    changestate(){
        this.setState(
            {showimage: false}
        )
    }
    changeCount(amount) {
        this.setState(prevState => {
        return { count: prevState.count + amount}
        })

    }
}
